mapper.update:
UPDATE board
SET mno=#{mno}, bdate=now(), btitle=#{btitle}, bcon=#{bcon}, bfile1=#{bfile1}, bfile2=#{bfile2}, bsort=1, betc=#{betc}
WHERE bno = #{bno}

UPDATE board 
SET bsort = 0
WHERE bno = #{bno}

mapper.select:
SELECT * FROM reply
WHERE bno = #{bno} 
ORDER BY reno DESC

mapper.update:
UPDATE reply
SET rewriter = #{rewriter}, rememo = #{rememo} , redate = now()
WHERE reno = #{reno}
