mapper.select:
SELECT COUNT(*) 
FROM member
WHERE mid = #{mid}

SELECT COUNT(*) 
FROM member 
WHERE mnick = #{mnick}

mapper.update:
UPDATE member 
SET mkey = #{param2}
WHERE mid = #{mid}

UPDATE member 
SET mkey = 'Y'
WHERE mid = #{mid}
AND mkey = #{param2}

mapper.select:
SELECT mno, mid, mnick, mclass
FROM member 
WHERE mid= #{mid} 
AND mpass = #{mpass}

SELECT mid
FROM member 
WHERE mname = #{mname} 
AND mmail = #{mmail}
